INSERT INTO tools_catalog (title, category, color_label, tags, description, links, notes)
SELECT
  item->>'title' AS title,
  item->>'category' AS category,
  NULLIF(item->>'color', '') AS color_label,
  COALESCE(ARRAY(SELECT jsonb_array_elements_text(item->'tags')), '{}') AS tags,
  item->>'description' AS description,
  COALESCE(item->'links', '[]'::jsonb) AS links,
  item->>'notes' AS notes
FROM payload, LATERAL jsonb_array_elements(payload.data) AS item
ON CONFLICT (title) DO UPDATE
SET
  category = EXCLUDED.category,
  color_label = EXCLUDED.color_label,
  tags = EXCLUDED.tags,
  description = EXCLUDED.description,
  links = EXCLUDED.links,
  notes = EXCLUDED.notes;
